# --- info credit evaluator ---
HIGH_VALUE = {
    'math_task': 6,
    'research_result': 7,
    'engine_patch': 5,
    'generator_self_patch': 5,
    'module_emit': 4,
    'cortex_evolution_plan': 6
}
MED_VALUE = {
    'conversation_reply': 2,
    'conversation_ping': 1,
    'maintenance_event': 1
}

descs = run_ctx.get('descs') or []
score = 0
for d in descs:
    dt = d.get('desc_type')
    if dt in HIGH_VALUE:
        score += HIGH_VALUE[dt]
    elif dt in MED_VALUE:
        score += MED_VALUE[dt]
    else:
        score += 1
# on redonne le score dans le format que le noyau comprend
run_ctx['info_credit'] = score
return {'score': score}
