import time, json

def plan(ctx):
    streams = ctx.get('streams') or []
    out = []
    for s in streams:
        if 'inbox:task_' in s or 'task_request' in s or 'host_goal_hypothesis' in s:
            out.append({
                'desc_type': 'module_call',
                'module': 'skill_requirement_router.py',
                'task': 'auto-from-stream',
                'ts': time.time()
            })
    return out
