import os, json, time
HYP_PATH = 'mem/host_model/implicit_target_hypothesis.json'

def _load_hyp():
    if not os.path.exists(HYP_PATH): return None
    with open(HYP_PATH, 'r', encoding='utf-8') as f: return json.load(f)

def plan(ctx: dict) -> list[dict]:
    gen = ctx.get('gen',0)
    if gen % 13 != 0: return []
    hyp = _load_hyp()
    target = (hyp or {}).get('hyp') or 'unsure'
    out = []
    if target == 'mathward':
        out.append({
            'desc_type': 'cortex_evolution_plan',
            'target': 'research',
            'op': 'insert_frags',
            'frags': [
                { 'code': "# mathward frag
# prefer math_task, research_result" }
            ],
            'test_scenario': { 'desc_type':'math_task', 'goal':'auto_from_host_model' },
            'note': 'auto mathward from host delta'
        })
    elif target == 'structure':
        out.append({
            'desc_type': 'cortex_evolution_plan',
            'target': 'generator',
            'op': 'insert_frags',
            'frags': [
                { 'code': "# structure frag
# prefer planner/evaluator self-rewrite" }
            ],
            'test_scenario': { 'desc_type':'cortex_shape_probe' },
            'note': 'auto structure from host delta'
        })
    else:
        out.append({
            'desc_type': 'cortex_evolution_plan',
            'target': 'planner',
            'op': 'insert_frags',
            'frags': [
                { 'code': "# neutral frag
# keep generating research_task when stalled" }
            ],
            'note': 'neutral evo'
        })
    return out
