import os, json, time
INTENT_PATH = os.path.join('mem','designer_intent.json')

def run(ctx=None):
    if not os.path.exists(INTENT_PATH):
        return { 'desc_type': 'designer_intent_probe_idle', 'ts': time.time() }
    with open(INTENT_PATH,'r',encoding='utf-8') as f: intent = json.load(f)
    hyp = intent.get('hypothesis') or 'inconnu'
    # on fabrique un desc qui EXPLIQUE l hypothese au createur, en langage deja connu par le noyau
    return {
        'desc_type': 'host_message',
        'channel': 'intent_probe',
        'text': f"j ai infere que ton but etait: {hyp}. si ce n est pas ca, renvoie-moi un desc de correction.",
        'expected_reply_schema': { 'desc_type': 'designer_intent_correction', 'target': hyp },
        'ts': time.time()
    }
