import time
INTERESTING = {'cortex_evolution_plan','limit_shape_inferred','limit_shape_counterexample'}

def evaluate(run_ctx: dict) -> dict:
    descs = run_ctx.get('descs') or []
    score = 0
    details = []
    for d in descs:
        dt = d.get('desc_type')
        if dt == 'limit_shape_inferred':
            score += 5; details.append('inferred_limit')
        elif dt == 'cortex_evolution_plan':
            score += 3; details.append('tested_limit')
        elif dt == 'limit_shape_counterexample':
            score += 6; details.append('found_counterexample')
    return {'score': score, 'why': details, 'ts': time.time()}
