import time
TARGETS = {
  'limit_shape_mdl_best': 10,
  'transition_rule_set_written': 10,
  'cortex_evolution_plan': 3,
  'limit_shape_inferred': 5,
  'cortex_evolution_plan_result': 6,
  'host_detached_success': 10
}

def evaluate(run_ctx: dict) -> dict:
    ds = run_ctx.get('descs') or []
    score = 0; why = []
    for d in ds:
        dt = d.get('desc_type')
        if dt in TARGETS:
            score += TARGETS[dt]
            why.append(dt)
    return {'score': score, 'why': why, 'ts': time.time()}
