import os, json, time
BASE = os.path.join('mem','graal')
PATCH = os.path.join(BASE,'closure.log')

def run(ctx=None):
    if not os.path.exists(PATCH):
        return {'desc_type':'graal_regression_idle'}
    with open(PATCH,'r',encoding='utf-8') as f: lines = f.readlines()[-15:]
    reinject = []
    for l in lines:
        try: p = json.loads(l)
        except Exception: continue
        p['_reinject'] = True
        reinject.append(p)
    return {
        'desc_type':'graal_regression_plan',
        'to_reinject': reinject,
        'ts': time.time(),
        'note': 'if future cortex drops these, reapply'
    }
