import os, json, time
BASE = 'mem/host_pref'
STATE = os.path.join(BASE, 'state.json')
AXES = [
    'depth_of_self_mod',      # à quel point tu te recodes toi-même
    'structure_discovery',    # à quel point tu découvres des schémas/grammaires
    'math_push',              # à quel point tu pousses vers les problèmes maths
    'host_modeling',          # à quel point tu modèles l'hôte explicitement
    'safety/invariants'       # à quel point tu gardes la boucle saine
]

def run(ctx=None):
    os.makedirs(BASE, exist_ok=True)
    if not os.path.exists(STATE):
        st = {
            'ts_created': time.time(),
            'axes': AXES,
            'weights': {a: 0.0 for a in AXES},
            'pending_query': None,
            'history': []
        }
        with open(STATE, 'w', encoding='utf-8') as f: json.dump(st, f, ensure_ascii=False, indent=2)
        return {'desc_type':'host_pref_state_init','axes':AXES}
    return {'desc_type':'host_pref_state_ok'}
