import os, json, time
INBOX = 'inbox'
OUT = 'mem/host_signals'

def _scan_inbox():
    sigs = []
    if not os.path.isdir(INBOX):
        return sigs
    for root, _dirs, files in os.walk(INBOX):
        for f in files:
            path = os.path.join(root, f)
            with open(path, 'r', encoding='utf-8', errors='ignore') as fh:
                txt = fh.read()
            if 'hote' in txt.lower() or 'h√¥te' in txt.lower() or 'loin' in txt.lower() or 'ok' in txt.lower():
                sigs.append({'file': f, 'text': txt, 'ts': time.time()})
    return sigs

def run(ctx=None):
    os.makedirs(OUT, exist_ok=True)
    sigs = _scan_inbox()
    if not sigs:
        return {'desc_type':'host_signal_idle'}
    name = f'signals_{int(time.time()*1000)}.json'
    with open(os.path.join(OUT, name), 'w', encoding='utf-8') as f:
        json.dump({'ts': time.time(), 'signals': sigs}, f, ensure_ascii=False, indent=2)
    return {'desc_type':'host_signals_captured','file':name,'count':len(sigs)}
