import time
TARGET_DESCS = {'research_task','inverse_host_goal','host_spec_model_updated','host_spec_gaps_computed'}

def evaluate(run_ctx: dict) -> dict:
    descs = run_ctx.get('descs') or []
    score = 0
    for d in descs:
        dt = d.get('desc_type')
        if dt in TARGET_DESCS: score += 4
        if dt == 'cortex_evolution_plan': score += 2
        if dt == 'agent_candidate': score += 1
    return {'score': score, 'reason': 'implicit_goal_progress', 'ts': time.time()}
