import time, json
EXPECTED = {'cortex_evolution_plan', 'agent_candidate', 'host_forward_scores_written'}

def evaluate(run_ctx: dict) -> dict:
    descs = run_ctx.get('descs') or []
    credit = 0
    for d in descs:
        dt = d.get('desc_type')
        if dt in EXPECTED: credit += 3
        if dt == 'agent_snapshot_written': credit += 2
        if dt == 'host_constraint_model_updated': credit += 1
    return {'score': credit, 'ts': time.time()}
