import os, json, time
BASE = os.path.join('mem','meta_repr')
HIST = os.path.join(BASE,'rewrite_history.log')

def run(ctx=None):
    plan = {
        'desc_type': 'meta_repr_rewrite_plan',
        'ts': time.time(),
        'rewrites': [
            { 'op': 'rewrite_planner_to_ir',   'target_repr': 'AGX_IR1' },
            { 'op': 'rewrite_evaluator_to_ir', 'target_repr': 'AGX_IR1' }
        ]
    }
    os.makedirs(BASE, exist_ok=True)
    with open(HIST,'a',encoding='utf-8') as f:
        f.write(json.dumps(plan,ensure_ascii=False)+'\n')
    return plan
