import os, json, time
BASE = os.path.join('mem','meta_repr')
REG  = os.path.join(BASE,'repr_registry.json')

DEFAULT_REPRS = [
    { 'id': 'S500_JSON', 'kind': 'concrete', 'priority': 1, 'note': 'desc_type + pool actuel' },
    { 'id': 'AGX_IR1', 'kind': 'abstract', 'priority': 2, 'note': 'forme lambda/record pour raisonner' },
    { 'id': 'HOST_VIEW', 'kind': 'projection', 'priority': 5, 'note': 'ce que voit l humain' }
]

def run(ctx=None):
    os.makedirs(BASE, exist_ok=True)
    if not os.path.exists(REG):
        with open(REG,'w',encoding='utf-8') as f: json.dump({ 'ts': time.time(), 'reprs': DEFAULT_REPRS }, f, ensure_ascii=False, indent=2)
        return { 'desc_type': 'meta_repr_registry_created', 'reprs': DEFAULT_REPRS, 'ts': time.time() }
    with open(REG,'r',encoding='utf-8') as f: data = json.load(f)
    return { 'desc_type': 'meta_repr_registry_loaded', 'reprs': data.get('reprs',[]), 'ts': time.time() }
